


(function() {
    $(function() {

        var operate;

        $('.delete').click(function() {
            return operate($(this), 'destroy');
        });


        return operate = function(opObj, method) {
            var param, tpl;

            if(method === 'destroy'){
                param = opObj.data('param');
                tpl = '';
                tpl += '<div style="padding:5px 20px">';
                tpl += '<form method="POST" action="/api/host/delete">';
                tpl += '<div class="well">';
                tpl += '<p>чбошод ' + opObj.text() + '? </p>';
                tpl += '</div>';
                tpl += '<input type="hidden" value="' + param.id + '" name="id"/>';
                tpl += '</form>';
                tpl += '</div>';
                return apollo.formDialog(tpl, {
                    dialog: {
                        title: opObj.text()
                    }
                });
            }


        };




    });
}).call(this);